mapper.insert:
insert into building (id, name, residential_area, 
  units, has_elevator, has_underground
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{residentialArea,jdbcType=INTEGER}, 
  #{units,jdbcType=INTEGER}, #{hasElevator,jdbcType=INTEGER}, #{hasUnderground,jdbcType=INTEGER}
  )

mapper.select:
SELECT b.id, b.name, b.residential_area, r.name AS residentialAreaName, b.units, b.has_elevator, p.type_name AS hasElevatorName, b.has_underground, p2.type_name AS hasUndergroundName FROM building b LEFT JOIN residential_area r ON b.residential_area = r.id LEFT JOIN param_config p ON b.has_elevator = p.type_id LEFT JOIN param_config p2 ON b.has_underground = p2.type_id WHERE p.type = 'hasornot' AND p2.type = 'hasornot'
and b.id=#{id}
and b.residential_area=#{residentialArea}
and b.name LIKE '%${name}%'
limit #{start} ,#{length}

select count(*) from building
id=#{id}
and name LIKE '%${name}%'
