mapper.select:
SELECT h.id, h.`code`, rbu.residentialArea, rbu.residentialAreaName, rbu.building, rbu.buildingName, rbu.unit, rbu.unitName, h.floor, h.`name`, h.area, h.has_elevator, p.type_name AS hasElevatorName, h.nature, p2.type_name AS natureName, h.type, p3.type_name AS typeName, h.building_rise, h.unit_price, h.owner_name, h.owner_psptid, h.account_balance FROM house h INNER JOIN ( SELECT r.id AS residentialArea, r.`name` AS residentialAreaName, b.id AS building, b.`name` AS buildingName, u.id AS unit, u.`name` AS unitName FROM residential_area r, building b, unit u WHERE r.id = b.residential_area AND b.id = u.building ) rbu ON h.unit = rbu.unit LEFT JOIN param_config p ON h.has_elevator = p.type_id LEFT JOIN param_config p2 ON h.nature = p2.type_id LEFT JOIN param_config p3 ON h.type = p3.type_id WHERE p.type = 'hasornot' AND p2.type = 'house.nature' AND p3.type = 'house.type'
and h.code LIKE '%${code}%'
and rbu.residentialArea=#{residentialArea,jdbcType=VARCHAR}
and rbu.building=#{building,jdbcType=VARCHAR}
and rbu.unit=#{unit,jdbcType=VARCHAR}
and h.floor=#{floor,jdbcType=VARCHAR}
and h.name=#{name,jdbcType=VARCHAR}
and h.has_elevator=#{hasElevator,jdbcType=VARCHAR}
limit #{start} ,#{length}

select count(*) FROM house h INNER JOIN ( SELECT r.id AS residentialArea, r.`name` AS residentialAreaName, b.id AS building, b.`name` AS buildingName, u.id AS unit, u.`name` AS unitName FROM residential_area r, building b, unit u WHERE r.id = b.residential_area AND b.id = u.building ) rbu ON h.unit = rbu.unit LEFT JOIN param_config p ON h.has_elevator = p.type_id LEFT JOIN param_config p2 ON h.nature = p2.type_id LEFT JOIN param_config p3 ON h.type = p3.type_id WHERE p.type = 'hasornot' AND p2.type = 'house.nature' AND p3.type = 'house.type'
and h.code LIKE '%${code}%'
and rbu.residentialArea=#{residentialArea,jdbcType=VARCHAR}
and rbu.building=#{building,jdbcType=VARCHAR}
and rbu.unit=#{unit,jdbcType=VARCHAR}
and h.floor=#{floor,jdbcType=VARCHAR}
and h.name=#{name,jdbcType=VARCHAR}
and h.has_elevator=#{hasElevator,jdbcType=VARCHAR}
