mapper.update:
update repair_item
set repair_record = #{repairRecord,jdbcType=INTEGER},
  remark = #{remark,jdbcType=VARCHAR},
  price = #{price,jdbcType=DECIMAL}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, repair_record, remark, price
from repair_item
where id = #{id,jdbcType=INTEGER}
