mapper.select:
SELECT ra.*, s.`name` AS streetName, r.`name` AS regionName FROM residential_area ra LEFT JOIN street s ON s.id = ra.street LEFT JOIN region r ON s.region = r.id
and ra.id=#{id}
and ra.name LIKE '%${name}%'
limit #{start} ,#{length}

select count(*) from residential_area
id=#{id}
and name LIKE '%${name}%'
