mapper.insert:
insert into street (id, name, region
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{region,jdbcType=INTEGER}
  )

mapper.update:
update street
set name = #{name,jdbcType=VARCHAR},
  region = #{region,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, region
from street
where id = #{id,jdbcType=INTEGER}

select s.id, s.name,s.region ,r.name as regionName from street s left join Region r on s.region = r.id
s.id=#{id}
and s.name LIKE '%${name}%'
and s.region = region
limit #{start} ,#{length}
