mapper.insert:
insert into unit (id, name, building, 
  code, total_floor, house_per_floor
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{building,jdbcType=INTEGER}, 
  #{code,jdbcType=VARCHAR}, #{totalFloor,jdbcType=INTEGER}, #{housePerFloor,jdbcType=INTEGER}
  )

mapper.select:
select id, name, building, code, total_floor, house_per_floor
from unit
where id = #{id,jdbcType=INTEGER}

SELECT u.id, u.name, rb.residentialArea, rb.residentialAreaName, u.building, rb.buildingName, u.total_floor, u.house_per_floor FROM unit u LEFT JOIN ( SELECT r.id AS residentialArea, r. NAME AS residentialAreaName, b.id AS building, b. NAME AS buildingName FROM residential_area r, building b WHERE r.id = b.residential_area ) rb ON u.building = rb.building
and u.id=#{id}
and rb.residential_area=#{residentialArea}
and rb.building=#{name}
limit #{start} ,#{length}

select count(*) from unit u LEFT JOIN ( SELECT r.id AS residentialArea, r. NAME AS residentialAreaName, b.id AS building, b. NAME AS buildingName FROM residential_area r, building b WHERE r.id = b.residential_area ) rb ON u.building = rb.building
and u.id=#{id}
and rb.residential_area=#{residentialArea}
and rb.building=#{name}
