(function($){
    jQuery.fn.table = function(settings){
        baseOpt = {
            language: {
                "sProcessing": "处理中...",
                "sLengthMenu": "显示 _MENU_ 项结果",
                "sZeroRecords": "没有匹配结果",
                "sInfo": "显示第 _START_ 至 _END_ 项结果，共 _TOTAL_ 项",
                "sInfoEmpty": "显示第 0 至 0 项结果，共 0 项",
                "sInfoFiltered": "",
                "sInfoPostFix": "",
                "sSearch": "搜索:",
                "sUrl": "",
                "sEmptyTable": "表中数据为空",
                "sLoadingRecords": "载入中...",
                "sInfoThousands": ",",
                "oPaginate": {
                    "sFirst": "首页",
                    "sPrevious": "上页",
                    "sNext": "下页",
                    "sLast": "末页"
                },
                "oAria": {
                    "sSortAscending": ": 以升序排列此列",
                    "sSortDescending": ": 以降序排列此列"
                }
            },
            ajax:{
                type:'post',
                data:function (a) {
                    delete  a.columns;
                    delete  a.order;
                    delete a.search;
                    if(settings.searchForm){
                        var param = $("#"+settings.searchForm).serializeJSON()
                        $("#"+settings.searchForm +" select").each(function (index,item) {
                            if(!(param[$(this).attr("name")])||param[$(this).attr("name")]==-1){
                                //select 表单值为-1不提交
                                delete param[$(this).attr("name")]
                            }
                        })
                        $("#"+settings.searchForm +" input").each(function (index,item) {
                            if(!(param[$(this).attr("name")])||param[$(this).attr("name")].length<1){
                                //select 表单值为-1不提交
                                delete param[$(this).attr("name")]
                            }
                        })
                        $.extend(a,param)
                    }
                },
                dataSrc:'data'
            },
            // pageLength:20,
            // lengthChange:false,
            lengthMenu:[ [10, 25, 50,100, -1], [10, 25, 50,100, "全部"] ],
            processing:true,
            searching:false,
            ordering:false,
            "serverSide": true,//此模式下如：过滤、分页、排序的处理都放在服务器端进行。
        }
        $.extend(true,baseOpt,settings);
        return $(this).DataTable(baseOpt);
    }
})(jQuery);